mapper.insert:
	insert
        into tb_partner
         (
              POST_NO
            , MEM_NO
            , PARTNER_TYPE
            , PARTNER_ORIGINAL_IMG
            , PARTNER_CHANGE_IMG
            , PARTNER_DATE
            , PARTNER_VIEWS
            , PARTNER_TITLE
            , PARTNER_CONTENT
            , PARTNER_START_DATE
            , PARTNER_END_DATE
            , AREA_CATEGORY_NO
            , PARTNER_NUMBER
            , PARTNER_STATUS
         )
        values
        (
           SEQ_POST_NO.NEXTVAL
          , #{memNo}
          , #{partnerType }
          , #{partnerOriginalImg }
          , #{partnerChangeImg }
          , sysdate
          , 1
          , #{partnerTitle }
          , #{partnerContent }
          , #{partnerStartDate }
          , #{partnerEndDate }
          , #{areaCategoryNo }
          , #{partnerNumber }
          , 'Y'
        )

mapper.update:
update
	tb_partner
set PARTNER_TYPE = #{partnerType }
	, PARTNER_ORIGINAL_IMG	= #{ partnerOriginalImg }
	, PARTNER_CHANGE_IMG	= #{ partnerChangeImg }
	, PARTNER_TITLE			= #{ partnerTitle }
	, PARTNER_CONTENT		= #{ partnerContent }
	, PARTNER_START_DATE	= #{ partnerStartDate }
	, PARTNER_END_DATE		= #{ partnerEndDate }
	, AREA_CATEGORY_NO		= #{ areaCategoryNo }
	, PARTNER_NUMBER		= #{ partnerNumber }
where POST_NO = #{ postNo}
